pragma solidity ^0.8.20;

library BasisPointConstants {
    // Shared definitions of how we represent percentages and basis points
    uint16 public constant ONE_BPS = 100; // 1 basis point with 2 decimals
    uint32 public constant ONE_PERCENT_BPS = ONE_BPS * 100;
    uint32 public constant ONE_HUNDRED_PERCENT_BPS = ONE_PERCENT_BPS * 100;
}
","keccak256":"0xcaa3c2f2eaf2de426875dafae0956015204c7cf85939010129192c2da4aeb54e","license":"MIT"},"contracts/vaults/dstake/DStakeRouterV2RebalanceModule.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC4626 } from "@openzeppelin/contracts/interfaces/IERC4626.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";

import { IDStableConversionAdapterV2 } from "./interfaces/IDStableConversionAdapterV2.sol";
import { DStakeRouterV2Storage } from "./DStakeRouterV2Storage.sol";
import { IDStakeRouterV2Module } from "./interfaces/IDStakeRouterV2Module.sol";

/**
 * @title DStakeRouterV2RebalanceModule
 * @notice Handles rebalancing flows for {@link DStakeRouterV2} via delegatecall.
 * @dev Storage layout mirrors the router, including inherited OpenZeppelin base contracts. All functions assume they
 *      are executed through delegatecall from the router, which enforces access control.
 */
contract DStakeRouterV2RebalanceModule is DStakeRouterV2Storage, IDStakeRouterV2Module {
    using SafeERC20 for IERC20;
    using Math for uint256;

    // --- Errors (mirrors router) ---
    error AdapterNotFound(address strategyShare);
    error ZeroPreviewWithdrawAmount(address strategyShare);
    error AdapterAssetMismatch(address adapter, address expectedAsset, address actualAsset);
    error SlippageCheckFailed(address asset, uint256 actualAmount, uint256 requiredAmount);
    error ZeroInputDStableValue(address fromAsset, uint256 fromAmount);
    error InvalidAmount();
    error InvalidVaultConfig();
    error VaultNotActive(address vault);
    error VaultNotFound(address vault);
    error InsufficientActiveVaults();
    error NoLiquidityAvailable();

    // --- Events ---
    event StrategySharesExchanged(
        address indexed fromStrategyShare,
        address indexed toStrategyShare,
        uint256 fromShareAmount,
        uint256 toShareAmount,
        uint256 dStableAmountEquivalent,
        address indexed exchanger
    );
    event StrategiesRebalanced(
        address indexed fromVault,
        address indexed toVault,
        uint256 amount,
        address indexed initiator
    );

    struct ExchangeLocals {
        address fromAdapterAddress;
        address toAdapterAddress;
        IDStableConversionAdapterV2 fromAdapter;
        IDStableConversionAdapterV2 toAdapter;
        uint256 dStableValueIn;
        uint256 calculatedToStrategyShareAmount;
    }

    constructor(address dStakeToken_, address collateralVault_) DStakeRouterV2Storage(dStakeToken_, collateralVault_) {}

    // --- Module metadata ---

    function moduleMetadata()
        external
        view
        returns (bytes32 storageFingerprint, address dStakeTokenAddress, address collateralVaultAddress)
    {
        return (STORAGE_FINGERPRINT, _dStakeToken, address(_collateralVault));
    }

    // --- Rebalance entry points (delegate-called) ---

    function rebalanceStrategiesByShares(
        address fromStrategyShare,
        address toStrategyShare,
        uint256 fromShareAmount,
        uint256 minToShareAmount
    ) external {
        _rebalanceStrategiesByShares(fromStrategyShare, toStrategyShare, fromShareAmount, minToShareAmount);
    }

    function rebalanceStrategiesBySharesViaExternalLiquidity(
        address fromStrategyShare,
        address toStrategyShare,
        uint256 fromShareAmount,
        uint256 minToShareAmount
    ) external {
        if (fromShareAmount == 0) revert ZeroInputDStableValue(fromStrategyShare, 0);
        if (fromStrategyShare == address(0) || toStrategyShare == address(0)) revert ZeroAddress();

        ExchangeLocals memory locals;
        locals.fromAdapterAddress = _strategyShareToAdapter[fromStrategyShare];
        locals.toAdapterAddress = _strategyShareToAdapter[toStrategyShare];

        if (locals.fromAdapterAddress == address(0)) revert AdapterNotFound(fromStrategyShare);
        if (locals.toAdapterAddress == address(0)) revert AdapterNotFound(toStrategyShare);

        VaultConfig memory fromConfig = _getVaultConfig(fromStrategyShare);
        if (!_isVaultStatusEligible(fromConfig.status, OperationType.WITHDRAWAL)) {
            revert VaultNotActive(fromStrategyShare);
        }

        VaultConfig memory toConfig = _getVaultConfig(toStrategyShare);
        if (!_isVaultStatusEligible(toConfig.status, OperationType.DEPOSIT)) {
            revert VaultNotActive(toStrategyShare);
        }

        locals.fromAdapter = IDStableConversionAdapterV2(locals.fromAdapterAddress);
        locals.toAdapter = IDStableConversionAdapterV2(locals.toAdapterAddress);

        locals.dStableValueIn = locals.fromAdapter.previewWithdrawFromStrategy(fromShareAmount);
        if (locals.dStableValueIn == 0) revert ZeroInputDStableValue(fromStrategyShare, fromShareAmount);

        (address expectedToShare, uint256 tmpToAmount) = locals.toAdapter.previewDepositIntoStrategy(
            locals.dStableValueIn
        );
        if (expectedToShare != toStrategyShare)
            revert AdapterAssetMismatch(locals.toAdapterAddress, toStrategyShare, expectedToShare);
        locals.calculatedToStrategyShareAmount = tmpToAmount;

        if (locals.calculatedToStrategyShareAmount < minToShareAmount) {
            revert SlippageCheckFailed(toStrategyShare, locals.calculatedToStrategyShareAmount, minToShareAmount);
        }

        _collateralVault.transferStrategyShares(fromStrategyShare, fromShareAmount, address(this));
        IERC20(fromStrategyShare).forceApprove(locals.fromAdapterAddress, fromShareAmount);
        uint256 receivedDStable = locals.fromAdapter.withdrawFromStrategy(fromShareAmount);
        IERC20(fromStrategyShare).forceApprove(locals.fromAdapterAddress, 0);

        IERC20(_dStable).forceApprove(locals.toAdapterAddress, receivedDStable);
        (address actualToStrategyShare, uint256 resultingToShareAmount) = locals.toAdapter.depositIntoStrategy(
            receivedDStable
        );
        if (actualToStrategyShare != toStrategyShare)
            revert AdapterAssetMismatch(locals.toAdapterAddress, toStrategyShare, actualToStrategyShare);

        {
            uint256 previewValue = locals.toAdapter.previewWithdrawFromStrategy(resultingToShareAmount);
            uint256 dustAdjusted = locals.dStableValueIn > dustTolerance ? locals.dStableValueIn - dustTolerance : 0;
            if (previewValue < dustAdjusted) {
                revert SlippageCheckFailed(_dStable, previewValue, dustAdjusted);
            }
        }

        if (resultingToShareAmount < minToShareAmount) {
            uint256 shareShortfall = minToShareAmount - resultingToShareAmount;
            uint256 shortfallValue = shareShortfall.mulDiv(
                locals.dStableValueIn,
                locals.calculatedToStrategyShareAmount,
                Math.Rounding.Ceil
            );

            if (shortfallValue > dustTolerance) {
                revert SlippageCheckFailed(toStrategyShare, resultingToShareAmount, minToShareAmount);
            }
        }

        IERC20(_dStable).forceApprove(locals.toAdapterAddress, 0);

        emit StrategySharesExchanged(
            fromStrategyShare,
            toStrategyShare,
            fromShareAmount,
            resultingToShareAmount,
            locals.dStableValueIn,
            msg.sender
        );
    }

    function rebalanceStrategiesByValue(
        address fromVault,
        address toVault,
        uint256 amount,
        uint256 minToShareAmount
    ) external {
        if (amount == 0) revert InvalidAmount();
        if (fromVault == toVault) revert InvalidVaultConfig();

        VaultConfig memory fromConfig = _getVaultConfig(fromVault);
        VaultConfig memory toConfig = _getVaultConfig(toVault);

        if (fromConfig.status != VaultStatus.Active || toConfig.status != VaultStatus.Active) {
            revert VaultNotActive(fromConfig.status == VaultStatus.Active ? toVault : fromVault);
        }

        if (!_isVaultHealthyForDeposits(toVault)) revert VaultNotActive(toVault);
        if (!_isVaultHealthyForWithdrawals(fromVault)) revert VaultNotActive(fromVault);

        uint256 requiredVaultAssetAmount = IERC4626(fromVault).previewWithdraw(amount);
        _rebalanceStrategiesByShares(fromVault, toVault, requiredVaultAssetAmount, minToShareAmount);

        emit StrategiesRebalanced(fromVault, toVault, amount, msg.sender);
    }

    // --- Internal helpers (copies of router logic) ---

    function _rebalanceStrategiesByShares(
        address fromStrategyShare,
        address toStrategyShare,
        uint256 fromShareAmount,
        uint256 minToShareAmount
    ) internal {
        address fromAdapterAddress = _strategyShareToAdapter[fromStrategyShare];
        address toAdapterAddress = _strategyShareToAdapter[toStrategyShare];
        if (fromAdapterAddress == address(0)) revert AdapterNotFound(fromStrategyShare);
        if (toAdapterAddress == address(0)) revert AdapterNotFound(toStrategyShare);

        VaultConfig memory fromConfig = _getVaultConfig(fromStrategyShare);
        if (!_isVaultStatusEligible(fromConfig.status, OperationType.WITHDRAWAL)) {
            revert VaultNotActive(fromStrategyShare);
        }

        VaultConfig memory toConfig = _getVaultConfig(toStrategyShare);
        if (!_isVaultStatusEligible(toConfig.status, OperationType.DEPOSIT)) {
            revert VaultNotActive(toStrategyShare);
        }

        IDStableConversionAdapterV2 fromAdapter = IDStableConversionAdapterV2(fromAdapterAddress);
        IDStableConversionAdapterV2 toAdapter = IDStableConversionAdapterV2(toAdapterAddress);

        uint256 dStableAmountEquivalent = fromAdapter.previewWithdrawFromStrategy(fromShareAmount);
        if (dStableAmountEquivalent <= dustTolerance) {
            return;
        }
        _collateralVault.transferStrategyShares(fromStrategyShare, fromShareAmount, address(this));

        IERC20(fromStrategyShare).forceApprove(fromAdapterAddress, fromShareAmount);
        uint256 receivedDStable = fromAdapter.withdrawFromStrategy(fromShareAmount);
        IERC20(fromStrategyShare).forceApprove(fromAdapterAddress, 0);

        IERC20(_dStable).forceApprove(toAdapterAddress, receivedDStable);
        (address actualToStrategyShare, uint256 resultingToShareAmount) = toAdapter.depositIntoStrategy(
            receivedDStable
        );
        if (actualToStrategyShare != toStrategyShare) {
            revert AdapterAssetMismatch(toAdapterAddress, toStrategyShare, actualToStrategyShare);
        }
        if (resultingToShareAmount < minToShareAmount) {
            revert SlippageCheckFailed(toStrategyShare, resultingToShareAmount, minToShareAmount);
        }
        IERC20(_dStable).forceApprove(toAdapterAddress, 0);

        {
            uint256 previewValue = toAdapter.previewWithdrawFromStrategy(resultingToShareAmount);
            uint256 dustAdjusted = dStableAmountEquivalent > dustTolerance
                ? dStableAmountEquivalent - dustTolerance
                : 0;
            if (previewValue < dustAdjusted) {
                revert SlippageCheckFailed(_dStable, previewValue, dustAdjusted);
            }
        }

        emit StrategySharesExchanged(
            fromStrategyShare,
            toStrategyShare,
            fromShareAmount,
            resultingToShareAmount,
            dStableAmountEquivalent,
            msg.sender
        );
    }

    function _getVaultConfig(address vault) internal view returns (VaultConfig memory config) {
        if (!vaultExists[vault]) revert VaultNotFound(vault);
        return vaultConfigs[vaultToIndex[vault]];
    }

    function _isVaultStatusEligible(VaultStatus status, OperationType operationType) internal pure returns (bool) {
        if (operationType == OperationType.DEPOSIT) {
            return status == VaultStatus.Active;
        }

        return status == VaultStatus.Active || status == VaultStatus.Impaired;
    }

    function _isVaultHealthyForDeposits(address vault) internal view returns (bool healthy) {
        try IERC4626(vault).totalAssets() returns (uint256) {
            try IERC4626(vault).previewDeposit(1e18) returns (uint256 shares) {
                return shares > 0;
            } catch {
                return false;
            }
        } catch {
            return false;
        }
    }

    function _isVaultHealthyForWithdrawals(address vault) internal view returns (bool healthy) {
        try IERC4626(vault).totalAssets() returns (uint256) {
            uint256 vaultShares = IERC20(vault).balanceOf(address(_collateralVault));
            if (vaultShares == 0) return false;

            try IERC4626(vault).previewRedeem(vaultShares) returns (uint256 assets) {
                return assets > 0;
            } catch {
                return false;
            }
        } catch {
            return false;
        }
    }
}
","keccak256":"0x68f77a9f0ed376001fbcabcc564bf2aaf06fa1d9741309752f0f87a7d5d6fe45","license":"MIT"},"contracts/vaults/dstake/DStakeRouterV2Storage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { Pausable } from "@openzeppelin/contracts/utils/Pausable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

import { BasisPointConstants } from "../../common/BasisPointConstants.sol";
import { IDStakeCollateralVaultV2 } from "./interfaces/IDStakeCollateralVaultV2.sol";

/**
 * @title DStakeRouterV2Storage
 * @notice Shared storage layout for {@link DStakeRouterV2} and its delegatecall modules.
 * @dev Inherit this contract to guarantee storage slot alignment across the router and modules.
 */
abstract contract DStakeRouterV2Storage is AccessControl, ReentrancyGuard, Pausable {
    // --- Errors ---
    error ZeroAddress();

    // --- Immutable addresses ---
    address internal immutable _dStakeToken;
    IDStakeCollateralVaultV2 internal immutable _collateralVault;
    address internal immutable _dStable;

    // --- Mutable configuration ---
    uint256 public dustTolerance;
    uint256 public maxVaultCount;
    uint256 public depositCap;
    uint256 public settlementShortfall;
    uint256 public reinvestIncentiveBps;
    uint256 internal _withdrawalFeeBps;

    // --- Adapter routing ---
    mapping(address => address) internal _strategyShareToAdapter;
    address internal _defaultDepositStrategyShare;

    // --- Vault configuration ---
    enum OperationType {
        DEPOSIT,
        WITHDRAWAL
    }

    enum VaultStatus {
        Active,
        Suspended,
        Impaired
    }

    struct VaultConfig {
        address strategyVault;
        address adapter;
        uint256 targetBps;
        VaultStatus status;
    }

    VaultConfig[] public vaultConfigs;
    mapping(address => uint256) public vaultToIndex;
    mapping(address => bool) public vaultExists;

    // --- Module wiring ---
    address public governanceModule;
    address public rebalanceModule;

    // --- Constants ---
    uint256 public constant MAX_REINVEST_INCENTIVE_BPS = BasisPointConstants.ONE_PERCENT_BPS * 20;
    uint256 public constant MAX_WITHDRAWAL_FEE_BPS = BasisPointConstants.ONE_PERCENT_BPS;
    bytes32 internal constant STORAGE_FINGERPRINT = keccak256("dtrinity.dstake.router.v2.storage:1");

    constructor(address dStakeToken_, address collateralVault_) {
        if (dStakeToken_ == address(0) || collateralVault_ == address(0)) {
            revert ZeroAddress();
        }

        _dStakeToken = dStakeToken_;
        _collateralVault = IDStakeCollateralVaultV2(collateralVault_);
        address dStableAddress = _collateralVault.dStable();
        if (dStableAddress == address(0)) {
            revert ZeroAddress();
        }
        _dStable = dStableAddress;

        // Defaults mirror the original router constructor.
        dustTolerance = 1;
        maxVaultCount = 10;
    }
}
","keccak256":"0x72d769967b4fc8a798a0a0b89572ea8a8a3438a01353aaf21d988b806653133f","license":"MIT"},"contracts/vaults/dstake/interfaces/IDStableConversionAdapterV2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IDStableConversionAdapterV2 Interface
 * @notice Interface for contracts that handle the conversion between the core stable asset (dUSD/dETH)
 *         and a specific yield-bearing or convertible ERC20 token (`strategy share`), as well as
 *         valuing that `strategy share` in terms of the stable asset.
 * @dev Implementations interact with specific protocols (lending pools, DEX LPs, wrappers, etc.).
 * @dev SECURITY: adapters must mint strategy shares straight to the collateral vault, burn (or transfer out) those
 *      shares before calling upstream withdraws, and rely on preview/rate helpers so wrapper supply cannot diverge from
 *      the underlying balance. See `WrappedDLendInvariant.test.ts` for the regression harness new adapters should copy.
 */
interface IDStableConversionAdapterV2 {
    /**
     * @notice Converts a specified amount of the stable asset into the specific `strategyShare`
     *         managed by this adapter.
     * @dev The adapter MUST pull `stableAmount` of the stable asset from the caller (expected to be the Router).
     * @dev The resulting `strategyShare` MUST be sent/deposited/minted directly to the `collateralVault` address provided during adapter setup or retrieved.
     * @param stableAmount The amount of stable asset to convert.
     * @return strategyShare The address of the specific `strategy share` token managed by this adapter.
     * @return strategyShareAmount The amount of `strategyShare` generated from the conversion.
     */
    function depositIntoStrategy(
        uint256 stableAmount
    ) external returns (address strategyShare, uint256 strategyShareAmount);

    /**
     * @notice Converts a specific amount of `strategyShare` back into the stable asset.
     * @dev The adapter MUST pull the required amount of `strategyShare` from the caller (expected to be the Router).
     * @dev The resulting stable asset MUST be sent to the caller.
     * @param strategyShareAmount The amount of `strategyShare` to convert.
     * @return stableAmount The amount of stable asset sent to the caller.
     */
    function withdrawFromStrategy(uint256 strategyShareAmount) external returns (uint256 stableAmount);

    /**
     * @notice Preview the result of converting a given stable amount to strategyShare (without state change).
     * @param stableAmount The amount of stable asset to preview conversion for.
     * @return strategyShare The address of the specific `strategy share` token managed by this adapter.
     * @return strategyShareAmount The amount of `strategyShare` that would be received.
     */
    function previewDepositIntoStrategy(
        uint256 stableAmount
    ) external view returns (address strategyShare, uint256 strategyShareAmount);

    /**
     * @notice Preview the result of converting a given strategyShare amount to stable asset (without state change).
     * @param strategyShareAmount The amount of `strategyShare` to preview conversion for.
     * @return stableAmount The amount of stable asset that would be received.
     */
    function previewWithdrawFromStrategy(uint256 strategyShareAmount) external view returns (uint256 stableAmount);

    /**
     * @notice Calculates the value of a given amount of the specific `strategyShare` managed by this adapter
     *         in terms of the stable asset.
     * @param strategyShare The address of the strategy share token (should match strategyShare()). Included for explicitness.
     * @param strategyShareAmount The amount of the `strategyShare` to value.
     * @return stableValue The equivalent value in the stable asset.
     */
    function strategyShareValueInDStable(
        address strategyShare,
        uint256 strategyShareAmount
    ) external view returns (uint256 stableValue);

    /**
     * @notice Returns the address of the specific `strategy share` token managed by this adapter.
     * @return The address of the `strategy share`.
     */
    function strategyShare() external view returns (address);

    /**
     * @notice Returns the underlying vault asset associated with this adapter (legacy compatibility helper).
     */
    function vaultAsset() external view returns (address);
}
","keccak256":"0xd98442e19119300779dc8f093c639ea0dab3d3dfb67580bfb84efb2a21ff888d","license":"MIT"},"contracts/vaults/dstake/interfaces/IDStakeCollateralVaultV2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IDStakeCollateralVaultV2 Interface
 * @notice Defines the external functions of the DStakeCollateralVaultV2 required by other
 *         contracts in the dSTAKE system, primarily the DStakeTokenV2.
 */
interface IDStakeCollateralVaultV2 {
    /**
     * @notice Calculates the total value of all managed `strategy shares` held by the vault,
     *         denominated in the underlying dStable asset.
     * @dev This is typically called by the DStakeTokenV2's `totalAssets()` function.
     * @return dStableValue The total value of managed assets in terms of the dStable asset.
     */
    function totalValueInDStable() external view returns (uint256 dStableValue);

    /**
     * @notice Returns the address of the underlying dStable asset the vault operates with.
     * @return The address of the dStable asset.
     */
    function dStable() external view returns (address);

    /**
     * @notice The DStakeTokenV2 contract address this vault serves.
     */
    function dStakeToken() external view returns (address);

    /**
     * @notice The DStakeRouter contract address allowed to interact.
     */
    function router() external view returns (address);

    /**
     * @notice Returns the strategy share at `index` from the internal supported list.
     */
    function supportedStrategyShares(uint256 index) external view returns (address);

    /**
     * @notice Returns the entire list of supported strategy shares. Convenient for UIs & off-chain analytics.
     */
    function getSupportedStrategyShares() external view returns (address[] memory);

    /**
     * @notice Transfers `amount` of `strategyShare` from this vault to the `recipient`.
     * @dev Only callable by the registered router.
     * @param strategyShare The address of the strategy share to send.
     * @param amount The amount to send.
     * @param recipient The address to receive the shares.
     */
    function transferStrategyShares(address strategyShare, uint256 amount, address recipient) external;

    /**
     * @notice Sets the address of the DStakeRouter contract.
     * @dev Only callable by an address with the DEFAULT_ADMIN_ROLE.
     * @param _newRouter The address of the new router contract.
     */
    function setRouter(address _newRouter) external;

    /**
     * @notice Adds a strategy share to the supported list. Callable only by the router.
     */
    function addSupportedStrategyShare(address strategyShare) external;

    /**
     * @notice Removes a strategy share from the supported list. Callable only by the router.
     */
    function removeSupportedStrategyShare(address strategyShare) external;

    /**
     * @notice Emitted when the router address is set.
     * @param router The address of the new router.
     */
    event RouterSet(address indexed router);

    /**
     * @notice Emitted when a new strategy share is added to the supported list.
     */
    event StrategyShareSupported(address indexed strategyShare);

    /**
     * @notice Emitted when a strategy share is removed from the supported list.
     */
    event StrategyShareRemoved(address indexed strategyShare);
}
