pragma solidity ^0.8.20;

import { IERC20 } from "contracts/dlend/core/dependencies/openzeppelin/contracts/IERC20.sol";
import { SafeERC20 } from "contracts/dlend/core/dependencies/openzeppelin/contracts/SafeERC20.sol";
import { IERC20Detailed } from "contracts/dlend/core/dependencies/openzeppelin/contracts/IERC20Detailed.sol";
import { IERC20WithPermit } from "contracts/dlend/core/interfaces/IERC20WithPermit.sol";
import { IPoolAddressesProvider } from "contracts/dlend/core/interfaces/IPoolAddressesProvider.sol";
import { IPool } from "contracts/dlend/core/interfaces/IPool.sol";
import { Rescuable } from "contracts/common/Rescuable.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { Pausable } from "@openzeppelin/contracts/utils/Pausable.sol";
import { IBaseOdosAdapter } from "./interfaces/IBaseOdosAdapter.sol";

/**
 * @title BaseOdosSwapAdapter
 * @notice Utility functions for adapters using Odos
 */
abstract contract BaseOdosSwapAdapter is Rescuable, Ownable, Pausable, IBaseOdosAdapter {
    using SafeERC20 for IERC20;

    /* State Variables */
    /// The address of the Aave PoolAddressesProvider contract
    IPoolAddressesProvider public immutable ADDRESSES_PROVIDER;

    /// The address of the Aave Pool contract
    IPool public immutable POOL;

    /**
     * @dev Constructor
     * @param addressesProvider The address of the Aave PoolAddressesProvider contract
     * @param pool The address of the Aave Pool contract
     */
    constructor(IPoolAddressesProvider addressesProvider, address pool) Ownable(msg.sender) {
        // Validate critical addresses are non-zero
        require(address(addressesProvider) != address(0), "AddressesProvider cannot be zero");
        require(pool != address(0), "Pool cannot be zero");

        ADDRESSES_PROVIDER = addressesProvider;
        POOL = IPool(pool);
    }

    /**
     * @dev Get the vToken, sToken and aToken associated to the asset
     * @param asset The address of the asset
     * @return address The address of the VariableDebtToken, vToken
     * @return address The address of the StableDebtToken, sToken
     * @return address The address of the aToken
     */
    function _getReserveData(address asset) internal view virtual returns (address, address, address);

    /**
     * @dev Supply an amount of asset to the Aave Pool
     * @param asset The address of the asset to be supplied
     * @param amount The amount of the asset to be supplied
     * @param to The address receiving the aTokens
     * @param referralCode The referral code to pass to Aave
     */
    function _supply(address asset, uint256 amount, address to, uint16 referralCode) internal virtual;

    /**
     * @dev Pull the ATokens from the user and withdraws the underlying asset from the Aave Pool
     * @param reserve The address of the asset
     * @param user The address of the user to pull aTokens from
     * @param amount The amount of tokens to be pulled and withdrawn
     * @param permitInput struct containing the permit signature
     */
    function _pullATokenAndWithdraw(
        address reserve,
        address user,
        uint256 amount,
        PermitInput memory permitInput
    ) internal returns (uint256) {
        (, , address aToken) = _getReserveData(reserve);

        // Handle permit front-running by checking allowance and using try-catch
        // If deadline is set to zero, assume there is no signature for permit
        if (permitInput.deadline != 0) {
            // Check current allowance before attempting permit
            uint256 currentAllowance = IERC20(aToken).allowance(user, address(this));

            // Only call permit if allowance is insufficient
            if (currentAllowance < amount) {
                // Wrap permit in try-catch to handle front-running gracefully
                try
                    permitInput.aToken.permit(
                        user,
                        address(this),
                        permitInput.value,
                        permitInput.deadline,
                        permitInput.v,
                        permitInput.r,
                        permitInput.s
                    )
                {
                    // Permit succeeded
                } catch {
                    // Permit failed (likely front-run). Re-check allowance.
                    // If front-runner's permit succeeded, allowance should now be sufficient
                    currentAllowance = IERC20(aToken).allowance(user, address(this));
                    if (currentAllowance < amount) {
                        // Allowance still insufficient after permit failure
                        revert("Permit failed and allowance insufficient");
                    }
                    // Otherwise, front-runner's permit succeeded, proceed with transfer
                }
            }
            // If currentAllowance >= amount, skip permit and proceed with transfer
        }

        uint256 aTokenBalanceBefore = IERC20(aToken).balanceOf(address(this));
        IERC20(aToken).safeTransferFrom(user, address(this), amount);
        uint256 aTokenBalanceDiff = IERC20(aToken).balanceOf(address(this)) - aTokenBalanceBefore;

        POOL.withdraw(reserve, aTokenBalanceDiff, address(this));
        return aTokenBalanceDiff;
    }

    /**
     * @dev Renews the asset allowance in case the current allowance is below a given threshold
     * @param asset The address of the asset
     * @param minAmount The minimum required allowance to the Aave Pool
     */
    function _conditionalRenewAllowance(address asset, uint256 minAmount) internal {
        uint256 allowance = IERC20(asset).allowance(address(this), address(POOL));
        if (allowance < minAmount) {
            IERC20(asset).safeApprove(address(POOL), type(uint256).max);
        }
    }

    /** Rescue Functions */

    /**
     * @dev Checks if the token is a restricted rescue token
     * @return bool True if the token is a restricted rescue token, false otherwise
     */
    function isRescuableToken(address) public pure override returns (bool) {
        // No restricted rescue tokens
        return false;
    }

    /**
     * @dev Rescues tokens accidentally sent to the contract
     * @param token Address of the token to rescue
     * @param receiver Address to receive the rescued tokens
     * @param amount Amount of tokens to rescue
     */
    function rescueToken(address token, address receiver, uint256 amount) public onlyOwner {
        // Expose the internal rescue token function of Rescuable
        _rescueToken(token, receiver, amount);
    }

    /**
     * @dev Rescues native tokens accidentally sent to the contract
     * @param receiver Address to receive the rescued tokens
     * @param amount Amount of tokens to rescue
     */
    function rescueNative(address receiver, uint256 amount) public onlyOwner {
        // Expose the internal rescue native function of Rescuable
        _rescueNative(receiver, amount);
    }

