pragma solidity ^0.8.20;

import "../interface/chainlink/IPriceFeed.sol";

/**
 * @title FixedPriceChainlinkAggregator
 * @notice A Chainlink-compatible oracle that returns a fixed price.
 * @dev Useful for assets that are designed to maintain a 1:1 peg with the base currency,
 *      such as frxETH/ETH where frxETH is redeemable 1:1 with ETH.
 *
 *      The fixed price is set at deployment and cannot be changed, ensuring immutability.
 *      The oracle always reports as "alive" since the price is constant and doesn't rely
 *      on external data sources that could go stale.
 */
contract FixedPriceChainlinkAggregator is IPriceFeed {
    int256 private immutable fixedPrice;
    uint8 private immutable priceDecimals;
    string public description;

    /**
     * @notice Constructs a fixed-price oracle with the specified price and decimals
     * @param _fixedPrice The fixed price to return (e.g., 1e8 for 1.00 with 8 decimals)
     * @param _decimals The number of decimals for the price (typically 8 for Chainlink compatibility)
     * @param _description Human-readable description of the price feed (e.g., "frxETH / ETH")
     */
    constructor(int256 _fixedPrice, uint8 _decimals, string memory _description) {
        require(_fixedPrice > 0, "Price must be positive");
        require(_decimals > 0 && _decimals <= 18, "Invalid decimals");
        fixedPrice = _fixedPrice;
        priceDecimals = _decimals;
        description = _description;
    }

    /**
     * @inheritdoc IPriceFeed
     */
    function decimals() external view override returns (uint8) {
        return priceDecimals;
    }

    /**
     * @inheritdoc IPriceFeed
     * @dev Always returns the fixed price with the current block timestamp.
     *      The roundId increments with each block to maintain compatibility with
     *      consumers that expect increasing round IDs.
     */
    function latestRoundData()
        external
        view
        override
        returns (uint80 roundId, int256 answer, uint256 startedAt, uint256 updatedAt, uint80 answeredInRound)
    {
        // Use block number as a pseudo-round ID for compatibility
        uint80 blockRoundId = uint80(block.number);
        return (blockRoundId, fixedPrice, block.timestamp, block.timestamp, blockRoundId);
    }

    /**
     * @notice Returns the fixed price value
     * @return The immutable fixed price set at deployment
     */
    function getFixedPrice() external view returns (int256) {
        return fixedPrice;
    }
}

